import { useState, useEffect } from "react";
import { Link } from "wouter";
import { collection, query, where, orderBy, onSnapshot } from "firebase/firestore";
import { db } from "@/lib/firebase";
import { ArrowLeft, ClipboardList } from "lucide-react";
import { Button } from "@/components/ui/button";
import OrderCard from "@/components/staff/order-card";
import ServiceAlert from "@/components/staff/service-alert";
import type { OrderEvent, ServiceRequestEvent } from "@shared/schema";

interface OrderWithId extends OrderEvent {
  id: string;
}

interface ServiceRequestWithId extends ServiceRequestEvent {
  id: string;
}

export default function StaffPanel() {
  const [orders, setOrders] = useState<OrderWithId[]>([]);
  const [serviceRequests, setServiceRequests] = useState<ServiceRequestWithId[]>([]);

  useEffect(() => {
    // Set up real-time listener for orders and service requests
    const q = query(
      collection(db, "events"),
      where("type", "in", ["order", "service-request"]),
      orderBy("timestamp", "asc")
    );

    const unsubscribe = onSnapshot(q, (querySnapshot) => {
      const ordersData: OrderWithId[] = [];
      const requestsData: ServiceRequestWithId[] = [];

      querySnapshot.docs.forEach((doc) => {
        const data = doc.data();
        if (data.type === "order") {
          ordersData.push({ ...data, id: doc.id } as OrderWithId);
        } else if (data.type === "service-request") {
          requestsData.push({ ...data, id: doc.id } as ServiceRequestWithId);
        }
      });

      setOrders(ordersData);
      setServiceRequests(requestsData);
    });

    return () => unsubscribe();
  }, []);

  return (
    <div className="min-h-screen bg-primary-dark">
      <header className="bg-secondary-dark p-3 sm:p-4 shadow-lg">
        <div className="max-w-7xl mx-auto flex items-center justify-between">
          <div className="flex items-center space-x-2 sm:space-x-3">
            <Link href="/">
              <Button variant="ghost" size="sm" className="text-gray-400 hover:text-white">
                <ArrowLeft size={16} />
              </Button>
            </Link>
            <div className="flex items-center space-x-2">
              <div className="w-6 h-6 sm:w-8 sm:h-8 bg-success-green rounded-lg flex items-center justify-center">
                <ClipboardList className="text-white" size={14} />
              </div>
              <h1 className="text-lg sm:text-xl font-bold">Staff Dashboard</h1>
            </div>
          </div>
          <div className="flex items-center space-x-2 sm:space-x-4">
            <div className="text-center">
              <p className="text-gray-400 text-xs sm:text-sm">Orders</p>
              <p className="text-success-green font-bold text-lg sm:text-xl">{orders.length}</p>
            </div>
            <div className="text-center">
              <p className="text-gray-400 text-xs sm:text-sm">Requests</p>
              <p className="text-warning-yellow font-bold text-lg sm:text-xl">{serviceRequests.length}</p>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-3 sm:px-4 py-4 sm:py-6">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 sm:gap-6">
          {/* Orders Column */}
          <div>
            <div className="flex items-center justify-between mb-4">
              <h2 className="text-2xl font-bold">New Orders</h2>
              <div className="flex items-center space-x-2">
                <div className="w-2 h-2 bg-success-green rounded-full animate-pulse"></div>
                <span className="text-success-green text-sm">Live</span>
              </div>
            </div>

            <div className="space-y-4">
              {orders.length === 0 ? (
                <div className="bg-secondary-dark rounded-xl p-6 text-center text-gray-400">
                  No active orders
                </div>
              ) : (
                orders.map((order) => (
                  <OrderCard key={order.id} order={order} />
                ))
              )}
            </div>
          </div>

          {/* Service Requests Column */}
          <div>
            <h2 className="text-2xl font-bold mb-4">Service Alerts</h2>

            <div className="space-y-4">
              {serviceRequests.length === 0 ? (
                <div className="bg-secondary-dark rounded-xl p-6 text-center text-gray-400">
                  No pending requests
                </div>
              ) : (
                serviceRequests.map((request) => (
                  <ServiceAlert key={request.id} request={request} />
                ))
              )}
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}

client/src/components/admin/add-item-mod.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Upload, X, Image as ImageIcon, Loader2 } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { uploadImage } from "@/lib/image-upload";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";

const addItemSchema = z.object({
  name: z.string().min(2, "Name must be at least 2 characters"),
  description: z.string().min(10, "Description must be at least 10 characters"),
  price: z.number().min(0.01, "Price must be greater than 0"),
  category: z.string().min(1, "Please select a category"),
  isVegetarian: z.boolean().default(false),
  isSpicy: z.boolean().default(false),
  preparationTime: z.number().min(1, "Preparation time must be at least 1 minute"),
});

type AddItemFormData = z.infer<typeof addItemSchema>;

interface AddItemModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

const categories = [
  "Starters",
  "Main Course", 
  "Desserts",
  "Beverages",
  "Snacks",
  "Salads"
];

export default function AddItemModal({ open, onOpenChange }: AddItemModalProps) {
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<AddItemFormData>({
    resolver: zodResolver(addItemSchema),
    defaultValues: {
      name: "",
      description: "",
      price: 0,
      category: "",
      isVegetarian: false,
      isSpicy: false,
      preparationTime: 15,
    },
  });

  const handleImageUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file type
      if (!file.type.startsWith('image/')) {
        toast({
          title: "Invalid file type",
          description: "Please select an image file (JPG, PNG, GIF)",
          variant: "destructive",
        });
        return;
      }

      // Validate file size (5MB max)
      if (file.size > 5 * 1024 * 1024) {
        toast({
          title: "File too large",
          description: "Please select an image smaller than 5MB",
          variant: "destructive",
        });
        return;
      }

      setImageFile(file);

      // Create preview
      const reader = new FileReader();
      reader.onload = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setImageFile(null);
    setImagePreview(null);
  };

  const addItemMutation = useMutation({
    mutationFn: async (itemData: any) => {
      const response = await fetch('/api/menu', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(itemData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      return response.json();
    },
    onSuccess: () => {
      // Immediately refresh menu data everywhere
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      queryClient.refetchQueries({ queryKey: ['/api/menu'] });

      toast({
        title: "Item added successfully!",
        description: "Menu item has been added and is now visible to customers",
      });

      // Clean interface - reset everything to default state
      form.reset({
        name: "",
        description: "",
        price: 0,
        category: "",
        isVegetarian: false,
        isSpicy: false,
        preparationTime: 15,
      });
      setImageFile(null);
      setImagePreview(null);
      onOpenChange(false);
    },
    onError: (error) => {
      console.error("Error adding menu item:", error);
      toast({
        title: "Error adding item",
        description: "Please try again later",
        variant: "destructive",
      });
    },
  });

  const onSubmit = async (data: AddItemFormData) => {
    setIsSubmitting(true);

    try {
      let imageUrl = "";

      // Upload image if one is selected
      if (imageFile) {
        try {
          console.log('Starting image upload...');
          imageUrl = await uploadImage(imageFile);
          console.log('Image upload successful:', imageUrl);
        } catch (uploadError) {
          console.error('Image upload error:', uploadError);
          // Continue without image for now
          imageUrl = "";
        }
      }

      // Prepare item data for Supabase
      const newItem = {
        name: data.name,
        description: data.description,
        price: data.price,
        category: data.category,
        image: imageUrl,
        isVegetarian: data.isVegetarian,
        isSpicy: data.isSpicy,
        preparationTime: data.preparationTime,
        inStock: true,
        inventory: 100, // Default inventory
      };

      console.log('Saving item to Supabase:', newItem);
      addItemMutation.mutate(newItem);

    } catch (error) {
      console.error("Error preparing menu item:", error);
      toast({
        title: "Error adding item",
        description: "Please try again later",
        variant: "destructive",
      });
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[500px] bg-secondary-dark border-gray-700 max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-xl font-bold">Add New Menu Item</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            {/* Image Upload */}
            <div className="space-y-2">
              <label className="text-sm font-medium">Item Image</label>
              {imagePreview ? (
                <div className="relative">
                  <img
                    src={imagePreview}
                    alt="Preview"
                    className="w-full h-32 object-cover rounded-lg"
                  />
                  <Button
                    type="button"
                    variant="destructive"
                    size="sm"
                    className="absolute top-2 right-2"
                    onClick={removeImage}
                  >
                    <X size={16} />
                  </Button>
                </div>
              ) : (
                <div className="border-2 border-dashed border-gray-600 rounded-lg p-6 text-center">
                  <ImageIcon className="mx-auto h-12 w-12 text-gray-400" />
                  <div className="mt-2">
                    <label htmlFor="image-upload" className="cursor-pointer">
                      <span className="mt-2 block text-sm font-medium text-gray-300">
                        Click to upload image
                      </span>
                      <span className="block text-xs text-gray-400">
                        PNG, JPG, GIF up to 5MB
                      </span>
                    </label>
                    <input
                      id="image-upload"
                      type="file"
                      accept="image/*"
                      onChange={handleImageUpload}
                      className="hidden"
                    />
                  </div>
                </div>
              )}
            </div>

            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Item Name</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="e.g., Butter Chicken" 
                      className="bg-primary-dark border-gray-600"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Describe the dish, ingredients, and special features..."
                      className="bg-primary-dark border-gray-600 min-h-[80px]"
                      {...field} 
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="price"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Price (â‚¹)</FormLabel>
                    <FormControl>
                      <Input 
                        type="number"
                        step="0.01"
                        placeholder="0.00"
                        className="bg-primary-dark border-gray-600"
                        {...field}
                        onChange={(e) => field.onChange(parseFloat(e.target.value) || 0)}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="category"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Category</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="bg-primary-dark border-gray-600">
                          <SelectValue placeholder="Select category" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {categories.map((category) => (
                          <SelectItem key={category} value={category}>
                            {category}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="preparationTime"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Preparation Time (minutes)</FormLabel>
                  <FormControl>
                    <Input 
                      type="number"
                      min="1"
                      className="bg-primary-dark border-gray-600"
                      {...field}
                      onChange={(e) => field.onChange(parseInt(e.target.value) || 15)}
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex items-center space-x-4">
              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="checkbox"
                  {...form.register("isVegetarian")}
                  className="rounded border-gray-600"
                />
                <span className="text-sm">Vegetarian</span>
              </label>

              <label className="flex items-center space-x-2 cursor-pointer">
                <input
                  type="checkbox"
                  {...form.register("isSpicy")}
                  className="rounded border-gray-600"
                />
                <span className="text-sm">Spicy</span>
              </label>
            </div>

            <div className="flex justify-end space-x-3 pt-4">
              <Button 
                type="button" 
                variant="ghost" 
                onClick={() => onOpenChange(false)}
                disabled={isSubmitting}
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="bg-accent-orange hover:bg-orange-600"
                disabled={isSubmitting}
              >
                {isSubmitting ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Adding...
                  </>
                ) : (
                  "Add Item"
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/admin/billing-tabl.content:
import { deleteDoc, doc } from "firebase/firestore";
import { db } from "@/lib/firebase";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import type { BillingRequestEvent } from "@shared/schema";

interface BillingRequestWithId extends BillingRequestEvent {
  id: string;
}

interface BillingTableProps {
  requests: BillingRequestWithId[];
}

export default function BillingTable({ requests }: BillingTableProps) {
  const { toast } = useToast();

  const printBill = (tableNumber: number) => {
    toast({
      title: "Printing bill",
      description: `Printing bill for Table ${tableNumber}...`,
    });
  };

  const markAsPaid = async (request: BillingRequestWithId) => {
    try {
      await deleteDoc(doc(db, "events", request.id));
      toast({
        title: "Payment processed",
        description: `Table ${request.table} has been marked as paid.`,
      });
    } catch (error) {
      console.error("Error marking as paid:", error);
      toast({
        title: "Error",
        description: "Failed to mark as paid. Please try again.",
        variant: "destructive"
      });
    }
  };

  const getTimeAgo = (): string => {
    // For now, returning a static value since we don't have timestamp parsing
    return "2 min ago";
  };

  if (requests.length === 0) {
    return (
      <Card className="bg-secondary-dark border-gray-700">
        <CardContent className="p-8 text-center text-gray-400">
          No billing requests pending
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="bg-secondary-dark border-gray-700">
      <CardContent className="p-6">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead>
              <tr className="border-b border-gray-700">
                <th className="text-left py-3 px-4 font-medium">Table</th>
                <th className="text-left py-3 px-4 font-medium">Request Time</th>
                <th className="text-left py-3 px-4 font-medium">Order Total</th>
                <th className="text-left py-3 px-4 font-medium">Payment Method</th>
                <th className="text-left py-3 px-4 font-medium">Actions</th>
              </tr>
            </thead>
            <tbody>
              {requests.map((request) => (
                <tr key={request.id} className="border-b border-gray-700">
                  <td className="py-4 px-4">
                    <div className="flex items-center space-x-2">
                      <div className="w-8 h-8 bg-accent-orange rounded-full flex items-center justify-center">
                        <span className="font-bold text-white text-sm">{request.table}</span>
                      </div>
                      <span className="font-medium">Table {request.table}</span>
                    </div>
                  </td>
                  <td className="py-4 px-4 text-gray-400">{getTimeAgo()}</td>
                  <td className="py-4 px-4 font-bold text-accent-orange">â‚¹740</td>
                  <td className="py-4 px-4">
                    <Badge className="bg-blue-500 text-white text-xs">UPI</Badge>
                  </td>
                  <td className="py-4 px-4">
                    <div className="flex space-x-2">
                      <Button
                        className="bg-accent-orange hover:bg-orange-600 text-white px-3 py-1 text-sm h-8"
                        onClick={() => printBill(request.table)}
                      >
                        Print Bill
                      </Button>
                      <Button
                        className="bg-success-green hover:bg-green-600 text-white px-3 py-1 text-sm h-8"
                        onClick={() => markAsPaid(request)}
                      >
                        Mark Paid
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </CardContent>
    </Card>
  );
}

