    staleTime: 10 * 1000, // 10 seconds
    refetchInterval: 15 * 1000, // Auto refresh every 15 seconds
  });

  // Fetch menu items from API  
  const { data: menuItems = [] } = useQuery({
    queryKey: ['/api/menu'],
    staleTime: 10 * 1000, // 10 seconds for real-time sync
    refetchInterval: 15 * 1000, // Auto refresh every 15 seconds
  });

  const todayOrders = (orders as any[]) || [];

  const todayRevenue = todayOrders.reduce((total, order) => {
    return total + order.items.reduce((orderTotal, item) => {
      const itemPrice = getItemPrice(item.id);
      return orderTotal + (itemPrice * item.quantity);
    }, 0);
  }, 0);

  const activeTables = new Set(todayOrders.map(order => order.table)).size;

  function getItemPrice(itemId: string): number {
    const prices: Record<string, number> = {
      'paneer-tikka': 280,
      'veg-samosa': 120,
      'chicken-seekh': 320,
      'garden-salad': 180,
    };
    return prices[itemId] || 0;
  }

  // Delete menu item mutation
  const deleteItemMutation = useMutation({
    mutationFn: async (itemId: string) => {
      const response = await fetch(`/api/menu/${itemId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        throw new Error('Failed to delete menu item');
      }

      return response.json();
    },
    onSuccess: () => {
      // Immediately refresh menu data everywhere
      queryClient.invalidateQueries({ queryKey: ['/api/menu'] });
      queryClient.refetchQueries({ queryKey: ['/api/menu'] });

      toast({
        title: "Item deleted successfully!",
        description: "Menu item has been removed from the menu",
      });
    },
    onError: (error) => {
      console.error('Error deleting menu item:', error);
      toast({
        title: "Error deleting item",
        description: "Please try again later",
        variant: "destructive",
      });
    },
  });

  const handleDeleteItem = (itemId: string, itemName: string) => {
    if (window.confirm(`Are you sure you want to delete "${itemName}"? This action cannot be undone.`)) {
      deleteItemMutation.mutate(itemId);
    }
  };

  const handleEditItem = (item: any) => {
    setSelectedItem(item);
    setShowEditItemModal(true);
  };

  return (
    <div>
      <div className="mb-6 sm:mb-8">
